import React, { useState, useEffect } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Label, Input, Col } from 'reactstrap';

export default function AddModal(props) {

  const [name, setName] = useState('');
  const [price, setPrice] = useState();
  const [tax, setTax] = useState();
  const [description, setDescription] = useState('');


  const values = {
    nombre: name,
    precio: price,
    iva: tax,
    descripcion: description
  };

  Axios.post(`${baseURL}/articulos`, values)
    .then(response => {
      console.log(response.data);
      // props.hideDialog();
      // setName("");
      // setPrice();
      // setTax();
      // setDescription();
    })
    .catch(error => {
      console.log(error);
    });

  function showAddModal() {
    props.getProducts()
    props.showAddModalProps()
    console.log(name)
    console.log(price)
    console.log(tax)
    console.log(description)
    console.log(values)
  }

  return (
    <Modal isOpen={props.addModal} toggle={showAddModal} size="lg">
      <ModalHeader toggle={showAddModal}>Añadir productos: </ModalHeader>
      <ModalBody>
        <FormGroup>
          <Col md="8">
            <Label >Nombre: </Label>
            <Input onChange={e => setName(e.target.value)} />
          </Col>
          <Col md="6">
            <Label>Precio unidad: </Label>
            <Input onChange={e => setPrice(e.target.value)} />
          </Col>
          {/* <Col md="3">
            <Label>Cantidad </Label>
            <Input type="select" name="select" id="exampleSelect">
                <option>10</option>
                <option>20</option>
                <option>30</option>
                <option>40</option>
                <option>50</option>
              </Input>
          </Col> */}
          <Col md="3"><Label>IVA</Label><Input
            onChange={e => setTax(e.target.value)} /></Col>
          <Label for="exampleText">Descripción</Label>
          <Input type="textarea" name="text" id="exampleText"
            onChange={e => setDescription(e.target.value)} />
        </FormGroup>
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={showAddModal} >Añadir</Button>{' '}
        <Button color="neutral" onClick={showAddModal}>Cancel</Button>{' '}
      </ModalFooter>
    </Modal>
  )

}
